  apiKey: "AIzaSyD5VClIkr3UldiNRqs_hDDJ61KlwojyS2Q",
  authDomain: "form-57e82.firebaseapp.com",
  databaseURL: "https://form-57e82.firebaseio.com",
  projectId: "form-57e82",
  storageBucket: "form-57e82.appspot.com",
  messagingSenderId: "851711590645",
  appId: "1:851711590645:web:8b797f1496a9421669c5bc",
  measurementId: "G-26GYPYG86C"
});

export const auth = firebase.auth();
export const firestore = firebase.firestore();

provider.setCustomParameters({prompt: 'select_account'});

export const signWithGoogle = () => auth.signInWithPopup(firebase.auth.GoogleAuthProvider());

export default firebase;
