import axios from "axios";
import { Component } from "react";
import AdminLayout from "../../AdminLayout/AdminLayout";
import AccountCard from "./UI/AccountCard";

class AccountDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Accounts:null,
            Schemes: null,
        }
    
    }
    componentDidMount(){
        if(this.state.Accounts === null){
            axios.get(window.$domain + 'api/Accounts/GetAccountbyUserId/' + this.props.match.params.id)
            .then(
                resp=>{
                    this.setState({Accounts: resp.data});
                    console.log(this.state.Accounts);
                }
            )
            axios.get(window.$domain + 'api/MTServices')
            .then(
                resp=>{
                    this.setState({Schemes: resp.data});
                    console.log(this.state.Schemes);
                }
            )
            
        }
    }

    render(){
        console.log(this.props.match.params.id)
        return(
            <AdminLayout>
                <div className='jumbotron mt-3'>
                    <div className='row'>
                        <div className='col-md-3'>
                            <h5 >Participated Schemes</h5>
                            {
                                this.state.Schemes?this.state.Accounts.map(
                                    Account => this.state.Schemes.map(
                                        Scheme=> Scheme.id === Account.mtServiceId ? <AccountCard schemeName={Scheme.name} desc={Scheme.details}/>: ''
                                    ) 
                                ): ''
                            }
                        </div>
                        <div className='col-md-9'>
                            <h5 style={{textAlign: 'center'}}>Account Payment Details</h5>

                        </div>
                    </div>
                </div>
            </AdminLayout>
        );
    }
}
export default AccountDetails;
