import { Component } from "react";
import AdminLayout from "../../../AdminLayout/AdminLayout";
import axios from 'axios'
import Cookie from 'universal-cookie'
class PaymentHistory extends Component{
    state = {
        historyList: [],
        userName: []
    }
    
    componentDidMount(){
        
        axios.get(window.$domain + 'api/AccDetails/GetAccDetailsbyAccountId/' + this.props.match.params.id)
            .then(
                resp=>{
                    this.setState({historyList: resp.data});
                    this.state.historyList.map(
                        item=>{
                            axios.get(window.$domain + 'api/Users/' + item.postedByUserId)
                            .then(
                                resp=>{
                                    var newArray=this.state.userName;
                                    newArray.push({userName: resp.data.userName,userId: resp.data.id});
                                    this.setState({userName: newArray});
                                }
                            ) 
                        }
                    )
                }
            ) 
    }
     render(){
        return(
            <AdminLayout>
                <div className="jumbotron container-fluid overflow-auto">
                    <h2>Payment History</h2>
                <table className="table table-bordered">
                    <thead>
                        <tr >
                            <th>Date</th>
                            <th>Payment</th>
                            <th>Payment</th>
                        </tr>
                    </thead>
                    <tbody>
                         {
                             this.state.historyList !== null ?
                             this.state.historyList.map(payment=>{
                                 return <tr>
                                            <td>{payment.payingDate}</td>
                                            <td>{payment.payedAmount}</td>
                                            <td>{this.state.userName.map((u)=>{
                                                if(u.userId === payment.postedByUserId){
                                                    return u.userName;
                                                }
                                            })}</td>
                                        </tr>
                             }): 'we are loading your data'
                         }
                    </tbody>
                </table>
            </div>
            </AdminLayout>
        )
    }
    
}

export default PaymentHistory;
