import firebase from 'firebase/app'
import 'firebase/auth'
import 'firebase/firestore'

export const  auth = firebase.auth();

firebase.initializeApp({
  apiKey: 'AIzaSyAZdZGwvYnQrslKrYMTMaoIDaBzC2y_npA',
  authDomain: 'fiverr-proj-38f57.firebaseapp.com',
  databaseURL: 'https://fiverr-proj-38f57.firebaseio.com',
  projectId: 'fiverr-proj-38f57',
  storageBucket: 'fiverr-proj-38f57.appspot.com',
  messagingSenderId: '1014404158767',
  appId: '1:1014404158767:web:304a2c1b2307e5a3bc3dda'
});

export const Fauth = firebase.auth();
export const firestore = firebase.firestore();

const provider = firebase.auth.GoogleAuthProvider();
provider.setCustomParameters({prompt: 'select_account'});

export const signWithGoogle = () => Fauth.signInWithPopup(provider);

export default firebase;
